mapper.select:
SELECT A.product_pk AS productPk, B.category_nm AS categoryNm, A.product_nm AS productNm, A.memo, A.buying_check AS buyingCheck, A.buying_date AS buyingDate, A.created_at AS createdAt FROM t_purchase_product A JOIN t_category B ON A.category_pk = B.category_pk WHERE A.user_pk = #{userPk}
AND A.buying_check < 2
AND A.buying_check = 0
AND A.buying_check = 1
AND DATE(created_at) = #{createdAt}

mapper.update:
UPDATE t_purchase_product
SET category_pk = #{categoryPk}, product_nm = #{productNm}, memo= #{memo}
WHERE product_pk = #{productPk} AND user_pk = #{userPk}

mapper.delete:
DELETE FROM t_purchase_product

    product_pk = #{productPk}
    AND buying_check = 0 AND user_pk = #{userPk}

mapper.update:
UPDATE t_purchase_product
SET buying_check = 1, buying_date = NOW()
WHERE product_pk = #{productPk} AND user_pk = #{userPk}

UPDATE t_purchase_product
SET buying_check = 2
WHERE product_pk = #{productPk}
AND buying_check = 1 AND user_pk = #{userPk}
